import {UseState} from 'react'
import '../css/navbar.css'

function DropMenu (){
    const [navClass, setNavClass] = useState('drop-menu--mobile, active');
    const [open, setOpen] = useState(true);

    const handleEvent = () => {
        setOpen(() => open);

        if(navClass === 'drop-menu--mobile, active'){
            setNavClass(() => 'drop-menu--mobile')
        }else{
            setNavClass(() => 'drop-menu--mobile, active')
        };
    };

    return (
        <div className="drop-menu">

        </div>
    );
};

export default DropMenu;
