import React, { useContext, useEffect, useState } from "react";
import { CartContext } from "../contexts/products-context";
import { products } from "../data/products_data"
import ShopingCart from "../assets/icons/shoping-cart.tsx";
import ItemCart from "./itemCart.js";
import '../css/cart.css';

const Cart = () => {
    const [cartOpen, setCartOpen] = useState(false);
    const [productsLength, setProductsLength] = useState(0);

    const { cartItems } = useContext(CartContext);

    useEffect(() => {
        setProductsLength(
            cartItems.reduce((previous, current) => previous + current.amount, 0)
        );
    }, [cartItems]);

    const total = cartItems.reduce((previous, current) => previous + current.amount * current.price, 0);

    return(
        <div className="cartContainer" onClick={() => {setCartOpen(!cartOpen);}}>
            <div className="buttonCartContainer">
                <div className="buttonCart">
                    {!cartOpen ? (
                        <ShopingCart color={"white"} size={20}/>
                    ) : (
                        <ShopingCart color={"black"} size={20}/>
                    )}
                </div>
            </div>
            {!cartOpen && (
                <div className="productsNumber">{productsLength}</div>
            )}
            {cartItems && cartOpen && (
                <div>
                    <h2>Tu carrito</h2>
                    {cartItems.length === 0 ? <p>Tu carrito est√° vacio</p> : (
                        <>
                        <div>{cartItems.map((item, id) => (
                            <ItemCart key={id} item={item} />
                        ))}</div>
                        <p>{products.name}</p>
                        </>
                    )}

                    <h2>Total: ${total}</h2>
                </div>
            )}
        </div>
    )
}

export default Cart;
