import {useState} from 'react'
import logo from '../assets/images/coffee-cups.png'
import menu_icon from '../assets/icons/mobile_menu.svg'
import shoping_cart from '../assets/icons/shoping-cart.svg'
import Dropmenu from '../elements/dropmenu'
import '../css/navbar.css'

function Navbar(){
    const [navClass, setNavClass] = useState('drop-menu--mobile');
    const [open, setOpen] = useState(true);

    const links = [
        {id:'Sobre nosotros', name: 'about'},
        {id:'Nuestro café', name: 'products'}, 
        {id:'Nuestr@s aliad@s', name: 'partners'}
       ];

    const handleEvent = () => {
        setOpen(()=> !open);

        if(navClass === 'drop-menu--mobile'){
            setNavClass(() => 'drop-menu--mobile, active')
        }else{
            setNavClass(() => 'drop-menu--mobile')
        };
    };

    const navLinks = links.map(link => {
        return(
            <li><a href={link.name}>{link.id}</a></li>
        )
    });

    return (
        <div className="navbar">
            <div className='menu-icon--mobile'>
                <a href='/' onClick={handleEvent}><img src={menu_icon} /></a>
            </div>
            <div className="header-logo">
                <a href='/home'><img src= {logo} className="logo"/></a>
            </div>
            <div className='nav-menu'>
                <ul className='nav-menu--list'>
                    {navLinks}
                </ul>
            </div>
            <a href='#'>
                <div className='shoping-cart--wrap'>
                        <img src= {shoping_cart}/>
                        <span>1</span>
                </div>
            </a>
            <div className={navClass}>
                {open && <Dropmenu />}
            </div>
        </div>
    );
};


export default Navbar;
