import { cleanup } from "@testing-library/react";
import { Children, createContext, useEffect, useState } from "react";

const CartContext = createContext()

export const CartProvider = ({children}) => {
    const [carItems, SetCarItems] = useState(() => {
        try{
            const productsInLocalStorage = localStorage.getItem('cartProducts');
            return productsInLocalStorage ? JSON.parse(productsInLocalStorage) : [];
        }catch(error){
            return [];
        }
    });

    useEffect(() => {
        localStorage.setItem('cartProducts', JSON.stringify(carItems));
    }, [carItems]);

    const addItemToCart = (product) => {
        const inCart = carItems.find((productInCart) => productInCart.id === product.id);

        if(inCart){
            SetCarItems(
                carItems.map((productInCart) => {
                    if(productInCart.id === product.id){
                        return{...inCart, amount: inCart.amount + 1}
                    }else return productInCart
                })
            );   
        } else{
            SetCarItems([...carItems, {...product, amount: 1}])
        }}

        const deleteItemToCart = (product) => {
            const inCart = carItems.find(
                (productInCart) => productInCart.id === product.id
            );
            if(inCart.amount === 1){
                SetCarItems(
                    carItems.filter(productInCart => productInCart.id !== product.id)
                );
            }else {
            SetCarItems((productInCart) => {
                if(productInCart.id === product.id){
                    return{...inCart, amount: inCart.amount - 1};
                }else return productInCart;
                });
            }
        };

        return(
            <CartContext.Provider value={{carItems, addItemToCart, deleteItemToCart}}>
                {children}
            </CartContext.Provider>
        );
    };
