import React from 'react'
import {useState} from 'react'
import Slider from "react-slick";
import '../css/products.css'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import {products} from '../elements/products_data'

function Products(){

    const [currentIndex, setCurrentIndex] = useState(0)

    const nextSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % products.length);
    };

    const prevSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + products.length) % products.length)
    };

    //slider setting
    var settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 4,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              initialSlide: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
      };

        const products_card = products.map((product, index) => {
            return(
                    <div key= {product.id} className={`product-card ${index === currentIndex ? `active`: `unactive-${product.id}`}`}>
                            <img class= 'product-image' src={product.image} alt='producto' />
                            <h3 className='product-name'>{product.name}</h3>
                            <p className='product-weight'>{product.weight}</p>
                            <a className='product-cta'>COMPRAR</a>
                    </div>
                )});

        return(
            <>
            <div className="products-section">
                <div className='products-container'>
                    <div className='prev-button' onClick={prevSlide}>
                        <span className='line-1'></span>
                        <span className='line-2'></span>
                    </div>
                        <div className='cards-container'>
                            {products_card}
                        </div>
                    <div className='next-button' onClick={nextSlide}>
                        <span className='line-1'></span>
                        <span className='line-2'></span>
                    </div>
                </div>
            </div>
            </>
        )
};

export default Products;
