import React, { useContext } from 'react';
import { products } from '../data/products_data';
import '../css/main_products.css'
import Navbar from '../elements/navbar';
import { Footer } from '../elements/footer';
import { CartContext, CartProvider } from '../contexts/products-context';

function Mainproducts(){
    <CartProvider>
    const { addItemToCart } = useContext(CartContext);
    return (
        <>
        <header className="App-header">
         <Navbar />
        </header>
        <section className="products-store">
            {products.map((product, id) =>
                <>
                <div className='products-wrap'>
                    <div key={id} className="store-card">
                        <img src={product.image} alt={product.name}/>
                        <p>
                        {product.name} - ${product.price}
                        </p>
                    </div>
                    <button className="add-button" onClick={() => addItemToCart(product)}>Agregar al carrito</button>
                </div>
                </>
            )}
            <div className='main-products--banner'>
                <h2>Â¡Descubre nuestros productos!</h2>
            </div>
        </section>
        <footer className='home-footer--wrap'>
            <Footer />
        </footer>
        </>
    );
    </CartProvider>
}

export default Mainproducts;
