import React, { useContext, useEffect, useState } from "react";
import { CartContext } from "../contexts/products-context";
import ShopingCart from "../assets/icons/shoping-cart.tsx";
import '../css/cart.css';

const Cart = () => {
    const [cartOpen, setCartOpen] = useState(false);
    const [productsLength, setProductsLength] = useState(0);

    const { carItems } = useContext(CartContext);

    useEffect(() => {
        setProductsLength(
            carItems.reduce((previous, current) => previous + current.amount, 0)
        );
    }, [carItems]);

    const total = carItems.reduce((previous, current) => previous + current.amount * current.price, 0);

    return(
        <div className="cartContainer">
            <div className="buttonCartContainer">
                <div className="buttonCart">
                    {!cartOpen ? (
                        <ShopingCart color={"grey"} size={20}/>
                    ) : (
                        <ShopingCart color={"black"} size={20}/>
                    )}
                </div>
            </div>
            {!cartOpen && (
                <div className="productsNumber">{productsLength}</div>
            )}
        </div>
    )
}

export default Cart;
