import React from 'react'
import {useState} from 'react'
import '../css/products.css'
import {products} from '../elements/products_data'

function Products(){

    const [currentIndex, setCurrentIndex] = useState(0)

    const nextSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % products.length);
    };

    const prevSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + products.length) % products.length)
    };
        const products_card = [];

        for (let i = 0; i <= 3; i++){
            products_card.push(
                products.map((product, index) => {
                return(
                <div key= {product.id} className={`product-card ${index === currentIndex ? `active-${product.id}`: 'unactive'}`}>
                        <img class= 'product-image' src={product.image} alt='producto' />
                        <h3 className='product-name'>{product.name}</h3>
                        <p className='product-weight'>{product.weight}</p>
                        <a className='product-cta'>COMPRAR</a>
                </div>
                );
            })
        )
        };
        return(
            <div className="products-section">
                <div className='products-container'>
                    <div className='prev-button' onClick={prevSlide}>
                        <span className='line-1'></span>
                        <span className='line-2'></span>
                    </div>
                        {products_card}
                    <div className='next-button' onClick={nextSlide}>
                        <span className='line-1'></span>
                        <span className='line-2'></span>
                    </div>
                </div>
            </div>
        );
};

export default Products;
