import React from 'react'
import {useState} from 'react'
import '../css/products.css'
import {products} from '../elements/products_data'

function Products(){

    const [currentIndex, setCurrentIndex] = useState(0)

    const nextSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % 3);
    };

    const prevSlide = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + 3) % 3)
    };
        const products_card = [];
        console.log(currentIndex)
        for (let i = 0; i <= 2; i++){
            products_card.push(
                    <div key= {products[i].id} className={`product-card ${products[i].id === currentIndex ? `active-${products[i].id}`: 'unactive'}`}>
                            <img class= 'product-image' src={products[i].image} alt='producto' />
                            <h3 className='product-name'>{products[i].name}</h3>
                            <p className='product-weight'>{products[i].weight}</p>
                            <a className='product-cta'>COMPRAR</a>
                    </div>
                )
            };
        return(
            <div className="products-section">
                <div className='products-container'>
                    <div className='prev-button' onClick={prevSlide}>
                        <span className='line-1'></span>
                        <span className='line-2'></span>
                    </div>
                        {products_card}
                    <div className='next-button' onClick={nextSlide}>
                        <span className='line-1'></span>
                        <span className='line-2'></span>
                    </div>
                </div>
            </div>
        );
};

export default Products;
